import React, { Component } from "react";
import axios from "../../hoc/axios-orders";
import Order from "../../components/Order/Order";
class Orders extends Component {
  state = {
    orders: [],
    loading: true,
  };
  componentDidMount() {
    axios
      .get("./orders.json")
      .then((res) => {
        this.setState({ loading: false });
      })
      .catch((err) => {
        this.setState({ loading: false });
      });
  }
  render() {
    return (
      <div>
        <Order />
      </div>
    );
  }
}
export default Orders;
